import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import styles from "./addProject.module.css";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import SelectedCoinsTable from "./selectedCoinsTable";
import { useDispatch } from "react-redux";
import { getCoins } from "../../adapters/apis";
import InputLabel from "@material-ui/core/InputLabel";
import Stack from "@mui/material/Stack";
import DateAdapter from "@mui/lab/AdapterMoment";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import moment from "moment";
import DesktopDatePicker from "@mui/lab/DesktopDatePicker";
import {
  ADD_Projection,
  EDIT_Projection,
} from "../../redux/projections/projection.types";
import Input from "@mui/material/Input";
const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 500,
  height: 500,
  overflowY: "scroll",
  bgcolor: "background.paper",
  p: 4,
};

export default function AddProjectModal({
  open,
  handleClose,
  projectView,
  projectionName,
}) {
  const rx_live = /^\d+(?:[.,]{1}\d*)?$/;
  const dispatch = useDispatch();
  const [value, setValue] = React.useState(null);
  const [currentPrice, setCurrentPrice] = React.useState(0);
  const [currentCoin, setCurrentCoin] = React.useState(null);
  const [showErr, setShowErr] = React.useState(false);
  const [erreur, setErreur] = React.useState("");
  const [projection, setProjction] = React.useState("");
  const [showQt, setShowQt] = React.useState(false);
  const [coins, setCoins] = React.useState([]);
  const [qt, setQt] = React.useState(0);
  const [startDate, setStartDate] = React.useState(moment(new Date()));
  const [endDate, setEndDate] = React.useState(moment(new Date()).add(1, "M"));
  const [selectedCoins, setSelectedCoins] = React.useState([]);
  React.useEffect(() => {
    const localCoins = localStorage.getItem("coins");

    if (!localCoins || JSON.parse(localCoins).length === 0)
      getCoins().then((res) => {
        setCoins(res.data);
        localStorage.setItem("coins", JSON.stringify(res.data));
      });
    else setCoins(JSON.parse(localCoins));

    setProjction(
      projectView.length !== 0 ? projectView[0].name : projectionName
    );
    setSelectedCoins(projectView.length !== 0 ? projectView[0].coins : []);
  }, [projectionName, projectView]);


  const addCoins = () => {
    selectedCoins.push({
      ...currentCoin,
      quantity: parseFloat(qt),
      currentPrice: parseFloat(currentPrice),
    });
    setSelectedCoins(selectedCoins);
    setCurrentCoin(null);
    setValue(null);
  };

  const deleteCoin = (index) => {
    selectedCoins.splice(index, 1);
    setSelectedCoins([...selectedCoins]);
  };
  const handelAfterAddQ = () => {
    if (qt <= 0 || isNaN(qt)) {
      setErreur("Amount must be greater than 0 !");
      setShowErr(true);
      return;
    }
    if (currentPrice <= 0 || isNaN(currentPrice)) {
      setErreur("Current Price must be greater than 0 !");
      setShowErr(true);
      return;
    }
    addCoins(qt);
    setQt(0);
    setCurrentPrice(0);
    setShowQt(false);
    setShowErr(false);
  };
  const addQuantity = (value) => {
    const exist = selectedCoins.some((elem) => elem.name === value.name);
    if (exist) {
      setErreur("Coin already added!");
      setShowErr(true);
      return;
    }

    setShowErr(false);
    setErreur("");
    setCurrentCoin(value);
    setShowQt(true);
  };
  const handleQtChange = (event) => {
    let val=event.target.value;
    val=val.replace(',','.')
    if (rx_live.test(val))
      setQt( val );
  };
  const handleChangeTable = (val, index, who) => {

      if (who === "quantity")
        selectedCoins[index].quantity = parseFloat(val);
      else 
        selectedCoins[index].currentPrice = parseFloat(val);
      setSelectedCoins(selectedCoins);

  };
  const handleCpChange = (event) => {
    let val=event.target.value;
    val=val.replace(',','.')
    if (rx_live.test(val))
      setCurrentPrice( val );
  };
  const handlePrjChange = (event) => {
    setProjction(event.target.value);
  };
  const clearForm = () => {
    setProjction("");
    setQt(0);
    setSelectedCoins([]);
    setCurrentPrice(0);
    setValue("");
  };
  const handleCloseModal = () => {
    if (projectView.length === 0) clearForm();
    handleClose();
  };
  const handleAddPrjoect = () => {
    if (selectedCoins.length === 0) {
      setErreur("You need to add coins!");
      setShowErr(true);
      return;
    }
    setShowErr(false);
    setErreur("");
    const project = {
      id: projectView[0]?.id,
      name: projection,
      coins: selectedCoins,
      startDate: startDate.toDate(),
      endDate: endDate.toDate(),
    };
    if (projectView.length !== 0)
      dispatch({ type: EDIT_Projection, payload: project });
    else dispatch({ type: ADD_Projection, payload: project });
    handleCloseModal();
  };

  const handleStartDateChange = (newValue) => {
    setStartDate(newValue);
  };
  const handleEndDateChange = (newValue) => {
    if (newValue.toDate().getTime() < startDate.toDate().getTime()) {
      setEndDate(endDate);
      setErreur("The end date must be greater than the start date!");
      setShowErr(true);
    } else {
      setShowErr(false);
      setErreur("");
      setEndDate(newValue);
    }
  };

  return (
    <div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style} className={styles["modal"]}>
          {showErr ? <p className={styles["error"]}>{erreur}</p> : ""}
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            Configure your projection, by adding assets to your portfolio:
          </Typography>

          <TextField
            sx={{ width: "100%" }}
            label="Projection"
            value={projection}
            onChange={handlePrjChange}
          />
          <LocalizationProvider dateAdapter={DateAdapter}>
            <Stack spacing={3}>
              {" "}
              <DesktopDatePicker
                label="Start Date"
                inputFormat="DD-MM-YYYY"
                value={startDate}
                onChange={handleStartDateChange}
                renderInput={(params) => <TextField {...params} />}
              />
              <DesktopDatePicker
                label="End Date"
                inputFormat="DD-MM-YYYY"
                value={endDate}
                onChange={handleEndDateChange}
                renderInput={(params) => <TextField {...params} />}
              />
            </Stack>
          </LocalizationProvider>
          <Autocomplete
            value={value}
            onChange={(event, newValue) => {
              if (typeof newValue === "string") {
                setValue({
                  name: newValue,
                });
                addQuantity(newValue);
              } else if (newValue && newValue.inputValue) {
                // Create a new value from the user input
                setValue({
                  name: newValue.inputValue,
                });
                addQuantity({ name: newValue.inputValue, symbol: "" });
              } else {
                setValue(newValue);
                if (newValue) addQuantity(newValue);
              }
            }}
            filterOptions={(options, params) => {
              const { inputValue } = params;
              if (inputValue.length === 0) return [];
              const filtered = options.filter(
                (item) =>
                  item.name.toLowerCase().startsWith(inputValue) ||
                  item.symbol.toLowerCase().startsWith(inputValue)
              );

              // Suggest the creation of a new value
              const isExisting = options.some(
                (option) => inputValue === option.name
              );
              if (inputValue !== "" && !isExisting) {
                filtered.push({
                  inputValue,
                  name: `Add "${inputValue}"`,
                });
              }

              return filtered;
            }}
            selectOnFocus
            clearOnBlur
            handleHomeEndKeys
            id="free-solo-with-text-demo"
            options={coins}
            getOptionLabel={(option) => {
              if (typeof option === "string") {
                return option;
              }
              if (option.inputValue) {
                return option.inputValue;
              }
              return option.name;
            }}
            renderOption={(props, option) => <li {...props}>{option.name}</li>}
            sx={{ width: "100%" }}
            freeSolo
            renderInput={(params) => <TextField {...params} label="Assets" />}
          />
          {showQt ? (
            <div className={styles["div-flex"]}>
              <InputLabel htmlFor="component-helper">Amount</InputLabel>

              <Input
                type="text"
                value={qt}
                placeholder="Amount"
                onChange={handleQtChange}
              />
              <InputLabel htmlFor="component-helper">Current price</InputLabel>

              <Input
                type="text"
                placeholder="Current price"
                value={currentPrice}
                onChange={handleCpChange}
              />

              <Button variant="contained" onClick={handelAfterAddQ}>
                add coin
              </Button>
            </div>
          ) : (
            ""
          )}
          {selectedCoins.length ? (
            <SelectedCoinsTable
              coins={[...selectedCoins]}
              handleChangeTable={handleChangeTable}
              deleteCoin={deleteCoin}
            />
          ) : (
            ""
          )}

          <div className={styles["modal-footer"]}>
            <Button variant="contained" onClick={handleAddPrjoect}>
              Save Changes
            </Button>
            <Button variant="outlined" onClick={handleCloseModal}>
              Close
            </Button>
          </div>
        </Box>
      </Modal>
    </div>
  );
}
