import { ADD_Projection, EDIT_Projection } from "./prjoction.types";

const INITIAL_STATE = {
  projctions: JSON.parse(localStorage.getItem("projctions")) || [],
  project: null,
};

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ADD_Projection:
      const project = action.payload;
      project.id = state.projctions.length;
      const projctions = [...state.projctions, project]
      localStorage.setItem("projctions",JSON.stringify(projctions))
      return {
        ...state,
        projctions,
      };
    case EDIT_Projection:
      const projects = state.projctions;
      const projectEdit = action.payload;
      projects[projectEdit.id] = projectEdit;

    console.log(projectEdit)
      localStorage.setItem("projctions",JSON.stringify(projects))
      return {
        ...state,
        projctions: projects,
      };

    default:
      return state;
  }
};

export default reducer;
