import * as React from "react";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Input from "@mui/material/Input";
import Paper from "@mui/material/Paper";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

function StyledRow({coin,index,deleteCoin,handleChangeTable}) {

  const rx_live = /^\d+(?:[.,]{1}\d*)?$/;
  const [qt,setQt] = React.useState(coin.quantity)
  const [cp,setCp] = React.useState(coin.currentPrice)

  const handleChange = (event,type) => {
    let val=event.target.value;
    val=val.replace(',','.')
    if (rx_live.test(val)){
      if (type === "quantity")
        setQt(val);
      else 
        setCp(val);

      handleChangeTable(val, index,type)

    }

  }

  return(
    <StyledTableRow >
              <StyledTableCell component="th" scope="row">
                {coin.name}
              </StyledTableCell>
              <StyledTableCell component="th" scope="row">
                {coin.symbol}
              </StyledTableCell>
              <StyledTableCell component="th" scope="row">
              {handleChangeTable != null ? (
                  <Input
                    type="text"
                    value={cp}
                    onChange={(e) => handleChange(e,"currentPrice")}
                  />
                ) : (
                  coin.currentPrice
                )}
              </StyledTableCell>
              <StyledTableCell component="th" scope="row">
                {handleChangeTable != null ? (
                  <Input
                    type="text"
                    value={qt}
                    onChange={(e) => handleChange(e,"quantity")}
                  />
                ) : (
                  coin.quantity
                )}
              </StyledTableCell>
              {deleteCoin != null ? (
                <StyledTableCell component="th" scope="row">
                  <DeleteForeverIcon onClick={() => deleteCoin(index)} />
                </StyledTableCell>
              ) : 
              <td/>}
            </StyledTableRow>
  );

}

export default function SelectedCoinsTable({
  coins,
  deleteCoin,
  handleChangeTable,
}) {


  return (
    <TableContainer component={Paper}>
      <Table aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Assets</StyledTableCell>
            <StyledTableCell>Symbol</StyledTableCell>
            <StyledTableCell>Current Price</StyledTableCell>
            <StyledTableCell>Amount</StyledTableCell>
            {deleteCoin != null ? (
              <StyledTableCell align="right">Options</StyledTableCell>
            ) :
            <td/>}
          </TableRow>
        </TableHead>
        <TableBody>
          {coins.map((coin, index) => (
              <StyledRow key={index} handleChangeTable={handleChangeTable} deleteCoin={deleteCoin} coin={coin} index={index} />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
